import React from "react";
import { ProductsPopupContainer } from "./products-popup.styles";
import { getProductsListByCategoryId } from "../../../utils/algorithms";
import {selectProductTypeList} from "../../../redux/product-type/product-type.selectors"

import ProductsPopupItem from "../products-popup-item/products-popup-item.component";
import {connect} from "react-redux";
import {createStructuredSelector} from "reselect";
const ProductsPopup = React.forwardRef(
  ({offsetWidth, categoryId, productTypeList }, ref) => {    
    const productTypesByCategoryId = productTypeList.filter(productType => productType.category.toString() === categoryId.toString());     

    return (    
      <ProductsPopupContainer offsetWidth={offsetWidth} ref={ref} >
        {productTypesByCategoryId.map((productType) => (
          <ProductsPopupItem
            key={productType._id}
            productLabel={productType.name}
            productTypeId={productType._id}
          />
        ))}
      </ProductsPopupContainer>
    );
  }
);
const  mapStateToProps = createStructuredSelector({
  productTypeList : selectProductTypeList,
})
export default connect(mapStateToProps)(ProductsPopup);
