import React from 'react'
import {CheckoutPageContainer} from "./checkout.styles";
import CheckoutOverview from "../../components/Checkout/checkout-overview/checkout-overview.component";
import Background from "../../components/Layout/background/background.component";
import MasterHeader from "../../components/Layout/master-header/master-header.component";
import CheckoutForm from "../../components/Checkout/check-out-form/check-out-form.component";
import UpdateUserForm from "../../components/Checkout/update-user-form/update-user-form.component";
import {Route, Redirect} from "react-router-dom";
import {createStructuredSelector} from "reselect";
import {selectCurrentUser, selectUserLoading} from "../../redux/user/user.selectors";
import {connect} from "react-redux";
const CheckoutPage = ({match,currentUser}) => { 
  const checkOutFormCondition = (currentUser) => {   
    const {information } = currentUser; 
    if(!information)
      return false;
    if( !information.firstName ||
      !information.lastName ||
      !information.city ||
      !information.district ||
      !information.ward ||
      !information.address ||
      !information.phone ||
      !information.email )
        return false ; 
        return true;
  }
  console.log(currentUser);
  console.log(checkOutFormCondition(currentUser))
  return (
    <CheckoutPageContainer>
      <MasterHeader/>
      <Background label="Checkout"/>
      {/* <CheckoutOverview/> */}
      <Route path={match.patch} exact render={ props => currentUser && checkOutFormCondition(currentUser) ?  <CheckoutForm {...props} /> : <Redirect to={`${props.match.path}/update-information`}/> } />       
      <Route path={`/${match.patch}/update-information`} exact render={props => <UpdateUserForm currentUser={currentUser} {...props}/>}/> 
    </CheckoutPageContainer>
  )
}
const mapStateToProps = createStructuredSelector({
  currentUser : selectCurrentUser
})
export default connect(mapStateToProps)(CheckoutPage)
