import sellerActionTypes from "./seller.types";

const INITIAL_STATE = {
  register: {},
  term: false,
  product: {},
};

export default (state = INITIAL_STATE, action) => {
  switch (state.type) {
    case sellerActionTypes.SAVE_REGISTER_FORM:
      return {
        ...state,
        register: { ...state.register, ...action.payload },
      };
    case sellerActionTypes.SAVE_TERM:
      return {
        ...state,
        term: action.payload,
      };
    case sellerActionTypes.SAVE_PRODUCT_FORM:
      return {
        ...state,
        product: { ...state.product, ...action.payload },
      };
    case sellerActionTypes.CLEAR_ALL:
      return {
        register: {},
        term: false,
        product: {},
      };
    default:
      return state;
  }
};
