import styled from "styled-components";
import Carousel from "react-bootstrap/Carousel"
export const CustomCarousel = styled(Carousel)`
  width : 90%;
  margin : auto;
  height:  250px;
  background-color : blue;
`

export const CarouselItem = styled(Carousel.Item)`
  width : 100%;
`
