import React, {useState, useEffect, memo} from "react";
import {
  ProductsPopupItemContainer,
  LinkProductType,
  CustomLink,
} from "./products-popup-item.styles";
const ProductsPopupItem = ({ productType, productList }) => {
  const [linkUrl, setLinkUrl] = useState(null);
  // useEffect(() => {
  //   const linkUrlArr = linkUrl.split("/");
  //   const encodeLink = encodeURIComponent(linkUrlArr[linkUrlArr.length-1]);
  //   const newLinkUrl = [...linkUrlArr.slice(0,-1), 
  // }, [productType.linkUrl])
  return (    
    <ProductsPopupItemContainer>
      <LinkProductType to={`${productType.linkUrl}`}>{productType.name}</LinkProductType>
      {productList.length ? (
        productList.map((product) => (
          <CustomLink key={product._id} title={product.name} to={product.linkUrl}>
            {product.name}
          </CustomLink>
        ))
      ): null}
    </ProductsPopupItemContainer>
  );
};
export default memo(ProductsPopupItem);
