import styled from "styled-components";

export const ProductImagesContainer = styled.div`
  display : flex ; 
  flex-direction : column ; 
  width : 100%;
`

export const BackgroundImageContainer = styled.div`
  width : 100%; 
  max-width : 600px;
  height : 500px;
  max-height: 600px;
`

export const Image = styled.img`
  width : 100%;
  height : 100%;

`
