import styled from "styled-components";

export const ShopGridOverviewContainer = styled.div`  
  display : flex ; 
  width : 100%; 
  padding : 2rem ; 
  justify-content : center;
`

export const Grid = styled.div`
  margin : 1rem auto;
  width : ${props => props.mobileView ? "100%" : props.tabletView && props.w25 ? "40%" :  props.tabletView && !props.w25 ? "60%" : props.w25 ? "25%" : "75%"};  
`
