import React, { useState , useEffect} from "react";
import {
  MasterBannerContainer,
  MainTitle,
  SubTitle,
  Notice,
  Button,
  Span,
  BackgroundContainer,
  BackgroundImage,
} from "./banner.styles";
import BgImage from "../../../assets/img/hero/banner.jpg";
import useWindowSize from "../../UI/useWindowSize/useWindowSize.util";
const MasterBanner = () => {
  const [width] = useWindowSize();
  const [isMobile, setIsMobile] = useState(width < 992);
  useEffect(()=>{
    if(width > 992){
      setIsMobile(false);
    }else{
      setIsMobile(true);
    }
  },[width])
  return (
    <MasterBannerContainer img={BgImage} isMobile={isMobile}>      
      <MainTitle>fruit fresh</MainTitle>
      <SubTitle>
        <Span>Vegetable</Span>
        <Span>100% Organic</Span>
      </SubTitle>
      <Notice>Free Pickup and Delivery Available</Notice>
      <Button>Shop Now</Button>
    </MasterBannerContainer>
  );
};

export default MasterBanner;
