import React, { useState, useEffect } from "react";
import { PaginationContainer, PageLink } from "./right-side-pagination.styles";
import { withRouter } from "react-router-dom";
import { setCurrentPage } from "../../../redux/category/category.actions";
import { connect } from "react-redux";
import { generatePagination } from "../../../utils/algorithms"
const Pagination = ({
  currentPage,
  numPages,
  match,
  history,
  location,
  setCurrentPage,
}) => {
  const [listPages, setListPages] = useState([]);
  let trackMaxListPage = 0;
  let trackMinListPage = 0 ;
  useEffect(() => {
    let listPages = generatePagination(currentPage, numPages);
    setListPages([...listPages]);
  }, [currentPage, numPages])

  const movePage = page => {
    const pathName = location.pathname.split("/")[1];
    history.push(`/${pathName}/products?page=${page}`);
    setCurrentPage(page);
  }
  const handlePageLink = (page) => {
    if(page === "nextExtends"){
      if(listPages.length > 8){
        listPages = listPages.filter((_,idx) => listPages[idx].to === currentPage || idx > 3)
      }
      let maxListPage = listPages[listPages.length-3].to;
      if(maxListPage + 3 < numPages){
        for(let i = maxListPage +1 ; i <= maxListPage + 3 ; i++){
          listPages.splice(listPages.length-2,0,{to : i, text : i});
        }
      }else{
        for (let i = maxListPage + 1 ; i <= numPages ; i++){
          listPages.splice(listPages.length-2,0,{to : i, text : i});
        }
      }
      setListPages([...listPages])
      return ; 
    }
    movePage(page);
  };
  const handleNextPage = e => {
    movePage(currentPage + 1 );
  }
  return (
    <PaginationContainer>
      <PageLink>Về trước</PageLink>
      {listPages.length &&
        listPages.map((page) => (
          <PageLink  onClick={() => handlePageLink(page.to)}>{page.text}</PageLink>
        ))}
      <PageLink onClick={handleNextPage}>Tiếp</PageLink>
    </PaginationContainer>
  );
};

const mapDispatchToProps = dispatch => ({
  setCurrentPage : page => dispatch(setCurrentPage(page))
})
export default connect(null, mapDispatchToProps)(withRouter(Pagination));
