import React from "react";
import {ToggleDrawerContainer, Dash} from "./toggle-drawer.styles";
import {setOpenDrawer} from "../../../redux/drawer/drawer.actions";
import {connect} from "react-redux";

const ToggleDrawer = ({onOpenDrawer}) => {
  return (
    <ToggleDrawerContainer onClick={onOpenDrawer} >
      <Dash/>
      <Dash/>
      <Dash/>
    </ToggleDrawerContainer>
  )
}

const mapDispatchToProps = dispatch => ({
  onOpenDrawer : dispatch(setOpenDrawer())
})

export default connect(null, mapDispatchToProps)(ToggleDrawer);
