import React, {useState, useEffect} from 'react'
import {ResponseItemWrapper, Avatar, Row, ResponseText, ReadMore} from "./response-item.styles";
import Moment from "react-moment";
import Button from "@material-ui/core/Button"
import { FcBusinessman } from "react-icons/fc";
import { connect } from "react-redux";
import { selectCurrentUser } from "../../../redux/user/user.selectors";
import { createStructuredSelector } from "reselect";
import { withRouter, Redirect, Link } from "react-router-dom";
import { AiOutlineLike, AiOutlineDislike } from "react-icons/ai";
const ReponseItem = ({product, response, currentUser}) => {
  const [text, setText] = useState("");
  const [readMore, setReadMore] = useState(false);
  const [showReadMore, setShowReadMore] = useState(false);
  useEffect(() => {   
    if (response.text.length > 200) {
      setShowReadMore(true);
      setText(response.text.substr(0, 200));    
    } else {
      setShowReadMore(false);
      setText(response.text);
    }
  }, [response]);

  const handleSetReadMore = () => {
    if (readMore) {
      setReadMore(false);
      setText(response.text.substr(0, 200));
    } else {
      setReadMore(true);
      setText(response.text);
    }
  };
  return (
    <ResponseItemWrapper>
      <Row>
        <Avatar src={`http://localhost:5000/images/${response.user.avatar}`} />
        <Row style={{ flexDirection: "column" }}>
        <ResponseText>
            <p>
              <strong>
                {response.user.local.name || response.user.google.name || response.user.facebook.name}{" "}
                {response.user._id === product.user._id ? (
                  <span style={{ color: "#3f51b5" }}>
                    Nhà bán hàng <FcBusinessman />
                  </span>
                ) : (
                  ""
                )}
              </strong>{" "}
              <span style={{ color: "rgba(0,0,0,0.75)" }}>
                <Moment format="DD-MM-YYYY HH:mm">{response.createdAt}</Moment>
              </span>{" "}
            </p>
            <p>
              {text}{" "}
              {showReadMore ? (
                <span>
                  {!readMore ? "..." : " " }
                  <ReadMore onClick={handleSetReadMore}>
                    {!readMore ? "Xem thêm" : "Thu gọn"}
                  </ReadMore>
                </span>
              ) : null}{" "}
            </p>
          </ResponseText>
          <div>
            <ButtonLink onClick={handleClickLikeButton}>
              <span style={{ fontSize: "1em", verticalAlign: "middle" }}>
                <AiOutlineLike />
              </span>{" "}
              ({comment.likes.length})
            </ButtonLink>
            <ButtonLink onClick={handleClickDislikeButton}>
              <span style={{ fontSize: "1em", verticalAlign: "middle" }}>
                <AiOutlineDislike />
              </span>{" "}
              ({comment.dislikes.length})
            </ButtonLink>
            {currentUser && currentUser._id !== comment.user._id || !currentUser ? <ButtonLink onClick={handleClickResponseCommentButton}>Trả lời</ButtonLink> :  null}
          </div>
          </Row>
      </Row>
    </ResponseItemWrapper>
  )
}
const mapStateToProps = createStructuredSelector({
  currentUser : selectCurrentUser
})
export default connect(mapStateToProps)(ReponseItem)
