import React from "react";
import styled from "styled-components";

export const MasterSearchContainer = styled.div`
  border : 1px solid #ccc; 
  display : flex ; 
  width : 100%;
  height : 100%; 
  align-items : center;  
  z-index: 300;
`

export const CategoryTypes = styled.div`
  padding : .5rem 1rem ;
  text-align:center;
  border-right : 1px solid #ccc;
`
export const SearchInput = styled.input`  
  width : ${({mobileView}) => mobileView ? "70%" : "auto"};
  flex-grow : 1 ;     
  border : none ;
  outline : none ;
`
export const SearchButton = styled.button`
  width : ${({mobileView}) => mobileView ? "30%" : "auto"};;
  outline : none ; 
  border : none ;
  height : 100%;
  font-size : 1em;
  color : white;
  background-color : #7fad39;
  padding : ${({mobileView}) => mobileView ? "0.25rem" : "0.5rem 1rem"};
  cursor : pointer;
  &:hover { 
    background-color : #7fbf1d;
  }
  transition : all .25s;
`
