import styled from "styled-components";
import {darken} from "polished"
export const ProductProfileTextContainer = styled.div`
  width : 100%; 
  padding :1rem 3rem;
  border-left : 1px solid #eee;
  height : 100% ; 
`
export const Title = styled.h2`
  font-size : 1.6em ;
  font-weight : bold ;  
`
export const Reviews = styled.div`
  margin: 1rem 0;
  display : flex ; 
  align-items : center;
  font-size : 0.95em;
`
export const Span = styled.span`
  color : ${({color}) => color ? color : "inherit" };
`
export const Price = styled.h4`
  color : #dd2222;
  font-size : 1.4em;
`

export const BriefTextsInfo = styled.ul` 
  margin : 1rem;
  line-height : 1.6;
`

export const TextItem = styled.li``
export const  ProductActions = styled.div`
  display : flex ; 
`
export const  ProductQuantity = styled.div`
  display : flex ; 
  justify-content : center;
  font-size : 1.2em;
`
export const  Button = styled.button`
  outline : none ; 
  border : none  ;
  background-color : ${({bgColor}) => bgColor ? bgColor : "#e8e8e8"};
  &:hover{
    background-color : ${({bgColor}) => bgColor ? darken("0.1",bgColor ) :  darken("0.1","#e8e8e8")};
  }
`
export const  Paragraph = styled.p``
