import styled from "styled-components";
export const ProductItemContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 30%;
  overflow:  hidden ; 
  position : relative; 
`;

export const ProductImageContainer = styled.div`
  margin: auto;
  width: 40%;
  display: flex;
  flex-direction: column;
  align-items: center;
`;
export const ProductImage = styled.img`
  width: 8rem;
  height: 8rem;
`;
export const ProductInfo = styled.div`
  display: flex;
  flex-direction: column;
  height : 100%;
  width: 60%;
  margin-left : 12px;
  text-align: left;  
  justify-content : center;
  line-height : 2; 
`;
export const ProductName = styled.span`
  width: 80%;
  font-weight: bold;
  font-size: 1.2em;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;
export const ProductDiscountPrice = styled.span`
  color: red;
  font-weight: bold;
  font-size: 1.1em;
  position : relative; 
`;

export const OriginalPrice = styled.span`
  font-size: ${({ discount }) => (discount ? "1em" : "1.1em")};
  text-decoration: ${({ discount }) => (discount ? "line-through" : "none")};
  color: ${({ discount }) => (discount ? "#ccc" : "000")};
  font-weight: bold;
`;

export const DiscountBrand = styled.div`
  position : absolute; 
  top: 13%;
  right : 8%;
  width : 25%;
  height: 1.5rem; 
  transform : rotate(45deg);
  background-color : #dd2222 ; 
  color : white ; 
  &:before{
    content: "";
    height: 0;
    width: 0;
    position: absolute;
    border: 10px solid blue;
    top: 0;
    left: -14%;
    border: 12px solid;  
    border-color: #dd2222 transparent #dd2222 transparent;
  };
  &:after{
    content: "";
    height: 0;
    width: 0;
    position: absolute;
    border: 10px solid blue;
    top: 0;
    right: -14%;
    border: 12px solid;  
    border-color: #dd2222 transparent #dd2222 transparent;
  }
`
