import styled from "styled-components";

export const StarScopeWrapper = styled.div`
  display : flex ; 
  flex-direction : column ; 
  justify-content : center; 
`

export const Title = styled.h4`
  color : #002984;
  font-size : 1em ; 
  font-weight:  normal;
  margin : 1rem 0;
  position : relative; 
  &::before{
    content : "",
    position :absolute; 
    top : 0;
    width : 100%; 
    height : 1px ; 
    background-color : blue; 
  }
`
