import styled from "styled-components";

export const CategoryMenuContainer = styled.div`
  display : flex ; 
  width : 100%;
  flex-direction : column;
  padding : 0 1rem;
  height : ${({show}) => show ? "100%" : 0}; 
  border : ${({show}) => show ? "1px solid #ccc" : "none"};
  transition : height 0.3s ;
  position :relative;
`
