import React, {useState, useEffect, useRef} from 'react'
import {TaskbarContentWrapper} from "./task-content-item.styles";
const TaskbarContent = ({content, show}) => {
  const contentRef = useRef(null);
  useEffect(() => {   
    // let curOverflow = contentRef.current.style.overflow; 
    // if(!curOverflow || !curOverflow === "visible"){
    //   curOverflow = "hidden";
    // }
    console.log(contentRef.current.style.lineHeight, contentRef.current.scrollHeight)
    let isOverflowing=  contentRef.current.offsetHeight < contentRef.current.scrollHeight
    // contentRef.current.style.overflow = curOverflow;


    console.log(isOverflowing)
  }, [contentRef]) 
  return (
    <TaskbarContentWrapper dangerouslySetInnerHTML={{__html : content}} ref={contentRef}>

    </TaskbarContentWrapper>
  )
}

export default TaskbarContent
