import React, {memo} from "react";
import { CustomLink } from "./dropdown-menu-content.styles";
const DropdownMenuContent = ({onMouseEnter,item}) => {
  return (
    <CustomLink
      onMouseEnter={(e) => handleMouseEnter(e, item._id)}
      to={item.linkUrl}
      style={{ fontWeight: "400", textTransform: "capitalize" }}
      active={item._id === activeLink}
    >
      {item.name}
    </CustomLink>
  );
};

export default memo(DropdownMenuContent);
