import React, {useState,useContext, useEffect} from 'react'
import {MasterSearchContainer, CategoryTypes, SearchInput, SearchButton} from "./master-search.styles";
import {FaArrowDown} from "react-icons/fa";
import Icon from "../../UI/custom-icon/custom-icon.component";
import AppContext from "../../../context/app-viewport.context";
const MasterSearch = () => {
  const [textSearch, setTextSearch] = useState("");  
  const [mobileView, setMobileView] = useState(window.innerWidth < 600);
  const width = useContext(AppContext);
  useEffect(() => {
    if(width < 600){
      setMobileView(true);
    }else{
      setMobileView(false);
    }
  }, [width])
  console.log(mobileView)
  return (
    <MasterSearchContainer mobileView={mobileView}>
      {!mobileView && <CategoryTypes>Categories <Icon icon={<FaArrowDown/>} /></CategoryTypes>}
      <SearchInput value={textSearch} onChange={(e) => setTextSearch(e.target.value)} style={{margin: mobileView ? 0 : "0 1rem", fontSize: mobileView ? "1em" : "1.2em"}}/>
      <SearchButton mobileView={mobileView}>Search</SearchButton>
    </MasterSearchContainer>
  )
}

export default MasterSearch
