import styled from "styled-components";

export const ProductItemContainer = styled.div`
  margin : 2.5rem auto;
  display : flex;
  flex-direction : column;
  justify-content :center;
  align-items : center;
  width : 15rem;
  height: 20rem; 
`
export const ProductItemImageContainer = styled.div`
  width : 100%; 
  height: 65%;
  position :relative;   
`

export const ProductDiscount = styled.div`
  position : absolute ; 
  display : flex ;
  justify-content : center;
  align-items : center;
  color : white ;
  width : 3rem;
  height :3rem ;
  background-color :#dd2222; 
  border-radius : 50%;
  top : 0;
  left : 7%;
`
export const ProductItemImage = styled.img`
  width : 100%;
  height : 100%;  
`
export const ProductItemText = styled.div`
  display : flex ; 
  flex-direction : column;
  text-align : center;
  margin : 1rem auto;
  font-size : 1.1em;
  line-height : 1.6;
`
export const ProductName = styled.p`

`
export const ProductPrice = styled.div`
  display : flex ; 
  flex-direction : column;
  justify-content : space-between;
  font-weight : bold ; 
  color : red;
`

export const ProductNewPrice = styled.span``
export const ProductOldPrice = styled.span`
  color : rgba(0,0,0,0.6);
  font-size : 1em;
  text-decoration : line-through;
`

export const ButtonsGroup = styled.div`
  position :absolute; 
  width : 100%;
  max-width : 12rem;
  display : flex ; 
  justify-content : space-around;
  bottom : 15%;
`

export const Button = styled.span``
