const CarouselBanner = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1
  };
  return (
    <CarouselBannerWrapper>
      <Slider {...settings}>

      </Slider>
    </CarouselBannerWrapper>
  )
}

export default CarouselBanner
