import React, { useState } from "react";
import { PaginationContainer, PageLink } from "./right-side-pagination.styles";
import { withRouter } from "react-router-dom";
import { setCurrentPage } from "../../../redux/category/category.actions";
import { connect } from "react-redux";
const Pagination = ({
  currentPage,
  numPages,
  match,
  history,
  location,
  setCurrentPage,
}) => {
  let listPages = [];
  if (numPages < 4) {
    for (let i = 1; i <= numPages; i++) {
      listPages.push(i);
    }
  } else if (currentPage === 1) {
    listPages = [
      { to: currentPage, text: currentPage },
      { to: currentPage + 1, text: `${currentPage+1}` },
      { to: currentPage + 2, text: `${currentPage+2}` },
      { to: currentPage + 3, text: `${currentPage+3}` },
      { to: "nextExtend", text : "..."},
    ];
    listPages.push(numPages);
  } else if (currentPage === numPages - 1) {
    listPages = [
      { to: "prevExtend", text : "..."},
      { to:  currentPage - 3, text: `${currentPage - 3 }` },
      { to: currentPage -2 , text: `${currentPage - 2}` },
      { to: currentPage -1 , text: `${currentPage - 1}` },
      { to: currentPage , text: `${currentPage}` },     
    ];
  } else if (currentPage - 1 === 1) {
    listPages = [     
      { to:  currentPage - 1, text: `${currentPage - 1 }` },
      { to: currentPage, text: `${currentPage}` },
      { to: currentPage + 1 , text: `${currentPage + 1}` },
      { to: currentPage + 2, text: `${currentPage + 2}` },    
    ];
  } else if (currentPage + 1 === numPages.length - 1) {
    listPages = [
      { to: "prevExtend", text : "..."},
      { to:  currentPage - 2, text: `${currentPage - 2 }` },
      { to: currentPage -1 , text: `${currentPage -1}` },
      { to: currentPage , text: `${currentPage}` },
      { to: currentPage + 1, text: `${currentPage + 1}` },      
    ];
  } else {
    if (currentPage - 2 <= 1 && currentPage + 2 <= numPages - 1) {
      for (let i = 1; i <= currentPage + 2; i++) {
        listPages.push({to : i, text : i});
      }
    } else if (currentPage - 2 <= 1 && currentPage + 2 >= numPages - 1) {
      for (let i = 1; i <= numPages - 1; i++) {
        listPages.push({to : i, text : i});
      }
    } else if (currentPage - 2 >= 1 && currentPage + 2 >= numPages - 1) {
      for (let i = currentPage - 2; i <= numPages - 1; i++) {
        listPages.push({ to : i , text : i });
      }
    } else {
      for (let i = currentPage - 2; i <= currentPage + 2; i++) {
        listPages.push({to : i , text : i});
      }

    }
  }

  const handlePageLink = (page) => {
    const pathName = location.pathname.split("/")[1];
    history.push(`/${pathName}/products?page=${page}`);
    setCurrentPage(page);
  };
  return (
    <PaginationContainer>
      <PageLink>Về trước</PageLink>
      {listPages.length &&
        listPages.map((page) => (
          <PageLink onClick={() => handlePageLink(page)}>{page}</PageLink>
        ))}
      <PageLink to="/?page=5">Tiếp</PageLink>
    </PaginationContainer>
  );
};
export default connect(null, mapDispatchToProps)(withRouter(Pagination));
