import React, {useState, useEffect} from "react";
import {
  Form,
  FormInline,
  FormGroup,
  Label,
  Input,
  Required,
  Select,
  Option,
  Button
} from "./edit-user-form.styles";
import {connect} from "react-redux";
import {updateUserInfoStart} from "../../../redux/user/user.actions"
const CheckoutForm = ({currentUser}) => {  
  const handleUpdateUserInfo = () => {

  }
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState(""); 
  const [city, setCity] = useState("");
  const [district , setDistrict] = useState("");
  const [ward, setWard] = useState("");
  const [address, setAddress] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  useEffect(() => {
    if(currentUser && currentUser.information){
      setFirstName(currentUser.information.first_name);
      setLastName(currentUser.information.last_name);     
      setCity(currentUser.information.city);
      setDistrict(currentUser.information.district);
      setWard(currentUser.information.ward);
      setAddress(currentUser.information.address);      
      setPhone(currentUser.information.phone);      
      setEmail(currentUser.information.email);      
    }
  }, [currentUser])
  return (
    <Form>
      <FormInline>
        <FormGroup>
          <Label>
            Họ<Required>*</Required>
          </Label>
          <Input name="fname" value={firstName}/>
        </FormGroup>
        <FormGroup>
          <Label>
            Tên<Required>*</Required>
          </Label>
          <Input name="lname" value={lastName}/>
        </FormGroup>
      </FormInline>    
      <FormGroup>
        <Label>Tỉnh/ Thành phố</Label>
        <Input name="city" value={city}/>
      </FormGroup>
      <FormGroup>
        <Label>Quận/ Huyện</Label>
        <Input name="district" value={district}/>
      </FormGroup>
      <FormGroup>
        <Label>Phường/ Xã</Label>
        <Input name="ward" value={ward}/>
      </FormGroup>
      <FormGroup>
        <Label>Số nhà, đường</Label>
        <Input name="address" value={address} />
      </FormGroup>
      <FormInline>
        <FormGroup>
          <Label>Số điện thoại</Label>
          <Input name="phone" value={phone} />
        </FormGroup>
        <FormGroup>
          <Label>Email</Label>
          <Input name="email" value={email}/>
        </FormGroup>
      </FormInline>
      {/* <Select >
        <Option value="#" disabled selected hidden>Phương thức thanh toán</Option>
        <Option value="cod">Tiền mặt (COD)</Option>
        <Option value="paypal">Thanh toán qua thẻ</Option>
      </Select> */}
      <Button style={{display :"block", margin: "0 auto"}} onClick={handleUpdateUserInfo}>Lưu thay đổi</Button>
    </Form>
  );
};

const  mapDispatchToProps = dispatch => ({
  updateUserInfo : (userInfo) => dispatch(updateUserInfoStart(userInfo))
})

export default connect(null, mapDispatchToProps)(CheckoutForm);
