import React, { useEffect, useState , useRef} from "react";
import ReactCountryFlag from "react-country-flag";
import {
  ToggleUserContainer,
  Text,
  UserOptionsList,
  RowInline,
  Avatar,
} from "./toggle-user.styles";
import {Link} from "react-router-dom"
import {AiFillSetting, AiOutlineHistory} from "react-icons/ai"
import {FiLogOut} from "react-icons/fi";
import {createStructuredSelector} from "reselect";
import {selectCurrentUser} from "../../../redux/user/user.selectors";
import {connect} from "react-redux"
const configOptions = {
  settings: { text: "Tài khoản", icon: () => <AiFillSetting/>, linkUrl : "/user/account" },
  orderedHistory: { text: "Lịch sử giao dịch", icon : () => <AiOutlineHistory/> , linkUrl : "/ordered-history"},
  logout: { text: "Đăng xuất", icon: () => <FiLogOut/> , linkUrl : "/logout" },
};

const ToggleUser = ({user}) => {  
  const [showDropdown, setShowDropdown] = useState(false);
  const userRef = useRef(null);
  const listOptions = Object.keys(configOptions).map(key => configOptions[key]);  
  useEffect(() => {
    function trackUserDropdown(e){
      if(userRef && userRef.current.contains(e.target)){      
        setShowDropdown(true);
      }else{
        setShowDropdown(false);
      }
    }
    document.addEventListener("mouseover", trackUserDropdown);
    return () => document.removeEventListener("mouseover", trackUserDropdown);
  },[])
  return (
    <ToggleUserContainer ref={userRef}>
      <Avatar src={`http://localhost:5000/images/${user.avatar}`} />
      <Text>{user.name}</Text>
      <UserOptionsList show={showDropdown}>
        {listOptions.map((option) => (
          <RowInline key={option.linkUrl}>
            <Link to={option.linkUrl} style={{color : "white"}}>{option.icon()} {option.text}</Link>
          </RowInline>
        ))}
      </UserOptionsList>
    </ToggleUserContainer>
  );
};
const mapStateToProps = createStructuredSelector({
  user : selectCurrentUser
})
export default connect(mapStateToProps)(ToggleUser);
