import styled from "styled-components";

export const Option = styled.span`
  padding: 0 1rem;
  width:  ${({ isMobile }) => (isMobile ? "100%" : "auto")};
  position: relative;
  padding: ${({ isMobile }) => (isMobile ? "0.5rem" : "0")};  
  & svg{
    font-size : 1.5em;  
  }
`;

export const RowInline = styled.div`
  width: 100%;
  display: flex;
  & > * {
    display: flex;
    width: 50%;    
  }
`;
