import React from "react";
import {ToggleDrawerContainer, Dash} from "./toggle-drawer.styles";
import {connect} from "react-redux";
const ToggleDrawer = ({setOpenDrawer}) => {
  return (
    <ToggleDrawerContainer onClick={setOpenDrawer.bind(this)}>
      <Dash/>
      <Dash/>
      <Dash/>
    </ToggleDrawerContainer>
  )
}

export default connect(null, mapDispatchToProps)(ToggleDrawer);
