import React, {useState, useEffect, useContext} from 'react'
import {CheckoutOverviewContainer,Title, CheckoutPreview, Grid} from "./checkout-overview.styles";
import CheckoutForm from "../checkout-form/checkout-form.component";
import OrderForm from "../order-form/order-form.component";
import AppContext from "../../../context/app-viewport.context";
import {createStructuredSelector} from "reselect";
import {selectCurrentUser} from "../../../redux/user/user.selectors"
import {connect} from "react-redux";
import {Redirect} from "react-router-dom"
const CheckoutOverview = ({currentUser}) => {
  const [mobileView, setMobileView] = useState(window.innerWidth < 768);
  const [tabletView, setTabletView] = useState(window.innerWidth < 992 && window.innerWidth >= 768);
  const width = useContext(AppContext);
  useEffect(() => {
    if (width < 768) {
      setMobileView(true);
    } else {
      setMobileView(false);
    }
    if (width < 992 && width >= 768) {
      setTabletView(true);
    } else {
      setTabletView(false);
    }
  }, [width]);
  const [toggleEditForm, setToggleEditForm] = useState(false);
  useEffect(() => {
    if(currentUser && currentUser.information && currentUser.information.lastname && currentUser.information.firstname && currentUser.information.email && currentUser.information.address && currentUser.city && currentUser.district && currentUser.ward && currentUser.phone ){
      setToggleEditForm(true);
    }else{
      setToggleEditForm(false);
    }
  }, [currentUser]);
  if(!currentUser){
    return <Redirect to={{pathname : "/auth", state : {from : match.path}}}/>
  }
  return (
    <CheckoutOverviewContainer>
      <Title>Billing Details</Title>
      <CheckoutPreview mobileView={mobileView} tabletView={tabletView}>
        <Grid w55 mobileView={mobileView} tabletView={tabletView}>
          <CheckoutForm/>
        </Grid>
        <Grid mobileView={mobileView} tabletView={tabletView}>
          <OrderForm/>
        </Grid>
      </CheckoutPreview>
    </CheckoutOverviewContainer>
  )
}

export default connect(mapStateToProps)(CheckoutOverview)
