import React from "react";
import {
  LatestProductsContainer,
  Title,
  Grid,
  Slot,
} from "./latest-products.styles";
import { getTop10LatestProducts } from "../../utils/algorithms";
import Slider from "react-slick";

const LatestProducts = () => {
  let latestProductsGroup = [];
  let accumulatorProducts = [];
  getTop10LatestProducts().forEach((product, index) => {
    if (index !== 0 && index % 3 === 0) {
      latestProductsGroup.push(accumulatorProducts);
      accumulatorProducts = [];
    }
    accumulatorProducts.push(product);
    if (index === getTop10LatestProducts.length - 1 && index % 3 !== 0) {
      latestProductsGroup.push(accumulatorProducts);
    }
  });
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };


  return (
    <LatestProductsContainer>
      <Title>sản phẩm gần đây</Title>

      {latestProductsGroup.forEach((groups) => 
          groups.map 
      )}
    </LatestProductsContainer>
  );
};

export default LatestProducts;
