import React, { useState } from "react";
import { PaginationContainer, PageLink } from "./right-side-pagination.styles";
import {withRouter} from "react-router-dom";
const Pagination = ({ currentPage, numPages,  match, history, location}) => {
  let listPages = [];
  if (currentPage === 1) {
    if (numPages >= 4) {
      listPages = [1, 2, 3, 4];
    } else {
      for (let i = 0; i < numPages; i++) {
        listPages.push(i + 1);
      }
    }
  }
  const handlePageLink = (page) => {
    const pathName = match.url.split("/")[0];
    console.log(pathName)
    history.push(`${pathName}/products?page=${page}`)
  }
  return (
    <PaginationContainer>
      <PageLink>Về trước</PageLink>
      {listPages.length &&
        listPages.map((page) => (
          <PageLink onClick={() => handlePageLink(page)}>{page}</PageLink>
        ))}
      <PageLink to="/?page=5">Tiếp</PageLink>
    </PaginationContainer>
  );
};

export default withRouter(Pagination);
