import React, {useEffect} from 'react'
import {ProductsPopupContainer} from "./products-popup.styles";
import {getProductsListByCategoryId} from "../../utils/algorithms";
import ProductsPopupItem from "../products-popup-item/products-popup-item.component";
const ProductsPopup = React.forwardRef( ({setIsPopUpHover,offsetWidth,categoryId},ref)  => {
  let productsDict = getProductsListByCategoryId(categoryId);
  useEffect(() => {
    console.log(ref)
  })
  return (
    <ProductsPopupContainer offsetWidth={offsetWidth} ref={ref} onMouseOut={e => setIsPopUpHover(false)} onMouseOver={e => setIsPopUpHover(true)}>
      {Object.keys(productsDict).map(productItem => (
        <ProductsPopupItem key={productItem} productLabel={productItem} productList={productsDict[productItem]}/>
      ))}
    </ProductsPopupContainer>
  )
})

export default ProductsPopup
