import styled from "styled-components";

export const CartOverViewContainer = styled.div`  
  width : ${props => props.mobileView || props.tabletView ? "95%" : "88%"};
  overflow : auto ;
  margin : 3rem auto;
`

export const Row = styled.div`
  width : 100%; 
  display : flex ; 
  justify-content : space-between;
`
