import styled from "styled-components";

export const Option = styled.span`
  padding : 0 1rem;
  width : 100%;
  position :relative;

    padding-top :  ${({isMobile}) => isMobile ? "1rem 0" : "0"} ;  
    border-top : ${({isMobile}) => isMobile ? "1px solid #eee" : "none"}


`

export const RowInline = styled.div`
  width : 100%;
  display : flex ;   
  & > *{
    display : flex ; 
    width : 50%;
  }  
`
