import React from 'react'
import {CheckoutPageContainer} from "./checkout.styles";
import CheckoutOverview from "../../components/Checkout/checkout-overview/checkout-overview.component";
import Background from "../../components/Layout/background/background.component";
import MasterHeader from "../../components/Layout/master-header/master-header.component";
import CheckoutForm from "../../components/Checkout/check-out-form/check-out-form.component";
import UpdateUserForm from "../../components/Checkout/update-user-form/update-user-form.component";
import {Route, Redirect} from "react-router-dom";
import {createStructuredSelector} from "reselect";
import {selectCurrentUser, selectUserLoading} from "../../redux/user/user.selectors";
import {connect} from "react-redux";
const CheckoutPage = ({match,currentUser,loading}) => { 

  const checkOutFormCondition = (currentUser) => {      
    if(!currentUser || 
      !currentUser.information ||
      !currentUser.information.firstName ||
      !currentUser.information.lastName ||
      !currentUser.information.city ||
      !currentUser.information.district ||
      !currentUser.information.ward ||
      !currentUser.information.address ||
      !currentUser.information.phone ||
      !currentUser.information.email )
        return false ; 
        return true;
  }
  if(loading && !currentUser){
    return  null ;
  }
  return (
    <CheckoutPageContainer>
      <MasterHeader/>
      <Background label="Checkout"/>
      {/* <CheckoutOverview/> */}
      <Route path={match.path} exact render={ props => currentUser && !loading && checkOutFormCondition(currentUser) ?  <CheckoutForm {...props} currentUser={currentUser} /> : <Redirect to={`${props.match.path}/update-information`}/> } />       
      <Route path={`/${match.path}/update-information`} exact render={props => <UpdateUserForm currentUser={currentUser} {...props}/>}/> 
    </CheckoutPageContainer>
  )
}
const mapStateToProps = createStructuredSelector({
  currentUser : selectCurrentUser,
  loading : selectUserLoading
})
export default connect(mapStateToProps)(CheckoutPage)
