import React, {useEffect} from 'react'
import {ShopGridPageContainer} from "./category.styles";
import ShopGridOverview from "../../components/ShopGrid/shop-grid-overview/shop-grid-overview.component";
import MasterHeader from "../../components/Layout/master-header/master-header.component"
import Background from "../../components/Layout/background/background.component"
import {fetchCategoryStart} from "../../redux/category/category.actions";
import {connect} from "react-redux"
const ShopGridPage = ({match, fetchCategory, location}) => {  
  useEffect(() => {
      let page = 1 ; 
      if(location.search){
        page = +location.search.split("=")[1];
      }
      if(match.params.categoryUrl){       
        fetchCategory(match.params.categoryUrl, page);
      }

  }, [fetchCategoryStart])
  return (
    <ShopGridPageContainer>             
      <MasterHeader/>
      <Background label={match.params.categoryUrl}/>   
      <ShopGridOverview/>
    </ShopGridPageContainer>
  )
}
const mapDispatchToProps = dispatch => ({
  fetchCategory : (path,page) => dispatch(fetchCategoryStart(path,page))
})
export default connect(null, mapDispatchToProps)(ShopGridPage)
