import React, { useEffect, useState } from "react";
import { LatestProductsWrapper } from "./products-top-rated.styles";
import { getTopRatedProducts } from "../../../utils/connectDB";
import ProductItem from "../../UI/product-item/product-item.component";


const ProductsTopRated = ({ mobileView, tabletView }) => {
  const [topRatedProducts, setTopRatedProduct] = useState([]);
  useEffect(() => {
    let _mounted = true;
    getTopRatedProducts().then((data) => {
      if (_mounted) {
        setTopRatedProduct(data);
      }
    });
    return () => (_mounted = false);
  }, [getTopRatedProducts]);

  return <LatestProductsWrapper>
    <ProductItem mobileView={mobileView} tabletView={tabletView} productList={topRatedProducts} title="Sản phẩm HOT"/>
  </LatestProductsWrapper>;
};

export default ProductsTopRated;
