  const categoryItems = Object.keys(categoriesData).map(key => categoriesData[key]);

  let categoriesGroup = [] ; 
  let categoriesPerPage = [];
  categoryItems.forEach( (item,index) => {
    categoriesPerPage.push(item)
    if(index !== 0 && index % 3 === 0 || index === categoryItems.length - 1){
      categoriesGroup.push(categoriesPerPage);
      categoriesPerPage = [];
