import React, {useEffect, useState} from "react";
import { ProductsPopupContainer } from "./products-popup.styles";
import { selectProductTypeList } from "../../../redux/product-type/product-type.selectors";
import { selectProductList } from "../../../redux/product/product.selectors";
import ProductsPopupItem from "../products-popup-item/products-popup-item.component";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
const ProductsPopup = ({
  offsetWidth,
  categoryId,
  productTypeList,
  productList,
}) => {
  const [productTypesByCategoryId, setProductTypesByCategoryId] = useState(null);
  useEffect(() =>{
    setProductTypesByCategoryId( productTypeList.filter(
      (productType) => productType.category.toString() === categoryId.toString()
    ))
  }, [])

  return (
    <ProductsPopupContainer offsetWidth={offsetWidth}>
      {productTypesByCategoryId && productTypesByCategoryId.map((productType) => (
          <ProductsPopupItem
            key={productType._id}
            productLabel={productType.name}
            productList={productList.filter(product => product.productType.toString() === productType._id.toString())}
          />
        ))}
    </ProductsPopupContainer>
  );
};
const mapStateToProps = createStructuredSelector({
  productTypeList : selectProductTypeList,
  productList : selectProductList
})
export default connect(mapStateToProps)(ProductsPopup);
