import React from "react";
import {
  LatestProductsContainer,
  Title,
  Grid,
  Slot,
  ProductImageContainer,
  ProductImage,
  ProductLabel,
  ProductName, 
  ProductDiscountPrice,
  OriginalPrice,
} from "./products-latest.styles";
import { getLastestProducts } from "../../utils/algorithms";
import Rating from '@material-ui/lab/Rating';
import Box from '@material-ui/core/Box';
import Slider from "react-slick";

let lastestProducts = getLastestProducts();
let latestProductsGroup = [];
let accumulatorProducts = [];
lastestProducts.forEach( (product,idx) => {
  if( (idx !== 0 && idx % 3 === 0 )  ){
    latestProductsGroup.push(accumulatorProducts);
    accumulatorProducts = [];
  }    
  accumulatorProducts.push(product)
  if( idx %3 !==0 && idx === lastestProducts.length -1 ){
    latestProductsGroup.push(accumulatorProducts);
    accumulatorProducts = [];
  }
})

const LatestProducts = () => { 
  const settings = {
    dots: false,
    infinite: true,
    speed: 500,    
    slidesToShow: 1,
    slidesToScroll: 1,  
    nextArrow : null ,
    prevArrow : null ,
    adaptiveHeight : true  ,
    autoplay : true
  };

  return (
    <LatestProductsContainer>
      <Title>sản phẩm gần đây</Title>
      <Slider {...settings} style={{height: "85%"}}  autoplaySpeed={3000}>
        {latestProductsGroup.map((groups, id) => (
          <Grid key={id}>
            {groups.map((product) => (
              <Slot key={product.userId}>
                <ProductImageContainer>
                  <ProductImage src={product.imageUrl}/>
                  <Box component="fieldset" mb={3} borderColor="transparent">                  
                    <Rating name="read-only" value={product.votes}  precision={0.5} readOnly />
                  </Box>
                </ProductImageContainer>
                <ProductLabel>
                   <ProductName >{product.name}</ProductName> 
                   {product.discount === 0 ? 
                    (<OriginalPrice>{product.price.toLocaleString("es-AR")} Đ</OriginalPrice>) : 
                    (
                      <React.Fragment>
                          <OriginalPrice discount>{product.price.toLocaleString("es-AR")} Đ</OriginalPrice>
                          <ProductDiscountPrice>{(product.price*(100-product.discount)/100).toLocaleString("es-AR")} Đ</ProductDiscountPrice>
                      </React.Fragment>
                    )
                   }
                </ProductLabel>
              </Slot>
            ))}
          </Grid>
        ))}
      </Slider>
    </LatestProductsContainer>
  );
};

export default LatestProducts;
