import styled from "styled-components";
import React from "react";

export const DeparmentContainer = styled.div`
  display : flex ; 
  flex-direction : column;
  padding : 0 1.2rem;
  margin-bottom : 1rem;
`

export const DeparmentLabel = styled.h3`
  text-align: center;
  font-weight : bold ; 
  font-size : 1.3em;  
`
