import styled from "styled-components";

export const ProductsPopupContainer = styled.div`
  position : fixed;  
  left : ${({offsetWidth}) => `calc(100vh-${offsetWidth})`};
  width : 60vh;
  height : 60vh;
  background-color : blue; 
  z-index: 500;
`
