import React, { useState, useRef, useEffect } from "react";
import { CustomCarousel, CarouselItem , CarouselItems} from "./categories-slider.styles";
import categoriesData from "../../data/category";
import Slider from "react-slick";
// const categoryItems = Object.keys(categoriesData).map(
//   (key) => categoriesData[key]
// );

// let categoriesGroup = [];
// let categoriesPerPage = [];
// categoryItems.forEach((item, index) => {
//   if (index !== 0 && index % 4 === 0) {
//     categoriesGroup.push(categoriesPerPage);
//     categoriesPerPage = [];
//   }
//   categoriesPerPage.push(item);
//   if (index === categoryItems.length - 1 && index % 4 !== 0) {
//     categoriesGroup.push(categoriesPerPage);
//   }
// });

function SampleNextArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={className}
      style={{ ...style, display: "block", background: "red" }}
      onClick={onClick}
    />
  );
}
function SamplePrevArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={className}
      style={{ ...style, display: "block", background: "green" }}
      onClick={onClick}
    />
  );
}

class CategoriesSlider extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      nav1: null,
      nav2: null
    };
  }
  componentDidMount() {
    this.setState({
      nav1: this.slider1,
      nav2: this.slider2
    });
  }
  render() {
    const settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1, 
      slidesToShow: 3,
      slidesToScroll: 1,
      nextArrow: <SampleNextArrow />,
      prevArrow: <SamplePrevArrow />
    };
    return (
      <div style={{width : "800px"}}>
       <h4>Second Slider</h4>
        <Slider
          asNavFor={this.state.nav1}
          ref={slider => (this.slider2 = slider)}
          slidesToShow={3}
          swipeToSlide={true}
          focusOnSelect={true}
          nextArrow={<SampleNextArrow/>}
        >
          <div>
            <h3>1</h3>
          </div>
          <div>
            <h3>2</h3>
          </div>
          <div>
            <h3>3</h3>
          </div>
          <div>
            <h3>4</h3>
          </div>
          <div>
            <h3>5</h3>
          </div>
          <div>
            <h3>6</h3>
          </div>
        </Slider>

      </div>
    );
  }
}

export default CategoriesSlider;
