import React, { useState, useEffect, useContext } from "react";
import {
  Form,
  FormInline,
  FormGroup,
  Label,
  Input,
  Required,
  Option,
} from "./form-create-product.styles";
import { makeStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
import FormHelperText from "@material-ui/core/FormHelperText";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import NativeSelect from "@material-ui/core/NativeSelect";
import AppContext from "../../../context/app-viewport.context";
import {
  getCategoryList,
  getProductsListByCategoryId,
} from "../../../utils/algorithms";
import category from "../../../data/category";

const categoryList = getCategoryList();

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 250,
    width: "100%",
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));

const FormCreateProduct = () => {
  const classes = useStyles();
  const [category, setCategory] = useState({
    _id: "",
    name: "",
    linkUrl: "",
    products: [],
  });
  const [productsGroup, setProductsGroup] = useState([]);
  const [productsType, setProductsType] = useState("");
  const [mobileView, setMobileView] = useState(window.innerWidth < 600);
  const [tabletView, setTabletView] = useState(
    window.innerWidth < 992 && window.innerWidth >= 660
  );
  const width = useContext(AppContext);
  useEffect(() => {
    if (width < 660) {
      setMobileView(true);
    } else {
      setMobileView(false);
    }
    if (width < 992 && width >= 660) {
      setTabletView(true);
    } else {
      setTabletView(false);
    }
  }, [width]);

  const handleCategory = (e) => {
    const  category  = categoryList.find(
      (item) => item._id === e.target.value
    );
    if (category) {
      setCategory({
        _id: category._id,
        name: category.name,
        linkUrl: category.linkUrl,
        products: category.products,
      });
    } else {
      setCategory({ _id: "", name: "", linkUrl: "", products: [] });
    }
  };
  return (
    <Form mobileView={mobileView} tabletView={tabletView}>
      <FormControl className={classes.formControl}>
        <InputLabel htmlFor="age-native-helper">Loại Sản phẩm</InputLabel>
        <NativeSelect
          value={category.name}
          onChange={handleCategory}
          inputProps={{
            name: "category",
            id: "category-native-helper",
          }}
        >
          <option aria-label="None" value="" />
          {categoryList.map((categoryItem) => (
            <option key={categoryItem._id} value={categoryItem._id}>
              {categoryItem.name}
            </option>
          ))}
        </NativeSelect>
      </FormControl>
      {/* <FormControl className={classes.formControl}>
        <InputLabel htmlFor="age-native-helper">Nhóm Sản phẩm</InputLabel>
        <NativeSelect
          value={productsType}        
          onChange={e => setProductsType(e.target.value)}
          inputProps={{
            name: 'products',
            id: 'products-native-helper',
          }}
          disabled={!productsGroup.length}
        >
          <option aria-label="None" value="" />
          {productsGroup.map((groupName) => (
            <option key={groupName} value={groupName}>{groupName}</option>
          ))}
        </NativeSelect>    
      </FormControl> */}
    </Form>
  );
};

export default FormCreateProduct;
