import React, {useEffect, useState} from 'react'
import {CommentReviewsItemWrapper, Row, Grid, Image,CommentText} from "./comment-reviews-item.styles";
import Moment from 'react-moment';
const CommentReviewsItem = ({comment}) => {
  const [readMore, setReadMore] = useState(false);  

  return (
    <CommentReviewsItemWrapper>      
      <Row>      
        <Image src={comment.user.avatar === "avatar-default.png" ? `http://localhost:5000/images/${comment.user.avatar}` : null}/>    
        <CommentText>
          <p><strong>{comment.user.name}</strong> - <Moment format="DD-MM-YYYY HH-mm">{comment.createdAt}</Moment></p>
        {comment.text}
        </CommentText>


      </Row>
    </CommentReviewsItemWrapper>
  )
}

export default CommentReviewsItem
