import React, {useEffect, useRef} from 'react'
import {CarouselBannerWrapper} from "./carousel-banners.styles";
import bannersData from "../../../data/banners"
import Banner from "../banner/banner.component";
import Slider from "react-slick";
const CustomArrow = (props) => {
  const { className, style, onClick } = props;
  return (
    <div
      className={className}
      style={{ ...style, display: "block", background: "white", opacity : "0.8" }}
      onClick={onClick}
    />
  )
}

const CarouselBanner = () => {
  const carouselRef = useRef(null);
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    nextArrow: <CustomArrow />,
    prevArrow: <CustomArrow />
  };

  useEffect(() => {
    console.log(carouselRef.current);
  },[carouselRef.current.clientHeight] )
  return (
    <CarouselBannerWrapper ref={carouselRef}>
      <Slider {...settings} style={{height: "100%"}}>
        {bannersData.map(bannerItem => (
          <Banner key={bannerItem.imageUrl} img={bannerItem.imageUrl} />
        ))}
      </Slider>
    </CarouselBannerWrapper>
  )
}

export default CarouselBanner
