import React, {useState, useEffect} from "react";
import {
  ProductsPopupItemContainer,
  Label,
  CustomLink,
} from "./products-popup-item.styles";
import {connect} from "react-redux";
import {createStructuredSelector} from "reselect";
import {selectProductList} from "../../../redux/product/product.selectors"
const ProductsPopupItem = ({ productLabel, productTypeId, productList }) => {
  const [products, setProducts] = useState([]);
  useEffect(() => {
    setProducts(productList.filter(product => product.productType.toString() === productTypeId.toString()))
  }, [productTypeId])
  return (

    <ProductsPopupItemContainer>
      <Label>{productLabel}</Label>
      {productList && productList.length < 10 ? (
        productList.map((product) => (
          <CustomLink title={product.name} to={product.linkUrl}>
            {product.name}
          </CustomLink>
        ))
      ) : productList && (
        <React.Fragment>
          {productList
            .filter((_, idx) => idx < 10)
            .map((product) => (
              <CustomLink title={product.name} to={product.linkUrl}>
                {product.name}
              </CustomLink>
            ))}
          <CustomLink to="/" style={{textAlign: "center", color: "blue"}}>Xem thÃªm...</CustomLink>
        </React.Fragment>
      )}
    </ProductsPopupItemContainer>
  );
};
const  mapStateToProps = createStructuredSelector({  
  productList : selectProductList
})
export default connect(mapStateToProps)(ProductsPopupItem);
