import styled from "styled-components";

export const ToggleLanguageContainer = styled.div`
  width : 100%;
  display : flex ; 
  align-items : center;
  cursor : pointer;   
  position :relative; 
`

export const Text = styled.span`
  margin-right : 0.5rem;
`

export const LanguageList = styled.div`
  position : absolute ; 
  top : 100%; 
  width : 100%; 
  background-color : #424242; 
  color : white ;
  display : flex ; 
  flex-direction : column;
`
