import styled from "styled-components";

export const SearchOverViewWrapper = styled.div`
  width : 100%;  
  padding : 2rem;
  display : flex ; 
  justify-content : center;
  align-items : center;
  flex-direction : column;
`
export const Grid = styled.div`
  width : 100%;   
  display : grid ; 
  grid-template-columns : 1fr 1fr 1fr 1fr; 
  grid-gap : 0.7rem;
`
