import React, { useState } from "react";
import {
  Select,
  Option,
  Button,
  CheckoutPaymentWrapper,
  Label
} from "./checkout-payment.styles";
import {createStructuredSelector} from "reselect";
import {connect} from "react-redux";
import {postCODPayment} from "../../../utils/connectDB"
const CheckoutPayment = ({ history, location, currentUser, cartItems }) => {
  const [payMethod, setPayMethod] = useState("cod");
  const handleCODPayment = () => {
    postCODPayment(currentUser, cartItems);
  }
  return (
    <CheckoutPaymentWrapper>      
      <Label>Chọn phương thức thanh toán (*)</Label>
      <Select value={payMethod} onChange={e => setPayMethod(e.target.value)}>       
        <Option value="cod">Tiền mặt (COD)</Option>
        <Option value="paypal">Thanh toán qua thẻ</Option>
      </Select>
    {payMethod === "cod" ? <Button onClick={handleCODPayment}>Hoàn tất</Button> : null}
    </CheckoutPaymentWrapper>
  );
};

export default CheckoutPayment;
