import { put, takeLatest, call, all } from "redux-saga/effects";
import { registerSuccess, registerFail } from "./user.actions";
import userActionTypes from "./user.types";
import axios from "axios";
import urls from "../../utils/urls/urls";
function* register({ payload: { name, email, password } }) {
  try {
    console.log(name, email, password);
    const data = yield axios.post(urls.REGISTER_URL, { name, email, password });

    console.log(data);
    // localStorage.setItem("userToken", token);
    // localStorage.setItem("expDate", new Date(Date.now() + 60 * 60 * 1000));
    // yield put(registerSuccess(user));
  } catch (error) {
    yield put(registerFail(error.message));
  }
}

function* onRegister() {
  yield takeLatest(userActionTypes.REGISTER_START, register);
}

export default function* userSaga() {
  yield all([call(onRegister)]);
}
