import styled from "styled-components";
import {darken} from "polished";
export const IconContainer = styled.span`  
  color : ${({color}) => color ? color : "#454545" } ;
  &:hover{
    color : ${({color}) => color ? darken(0.1, `${color.trim().toString()}`) : "black"}
  }
`
